mapper.select:
SELECT secretary.*, FROM tab_pb_dept_secretary secretary
JOIN sys_user u ON secretary.user_id =u.user_id AND u.del_flag = 0
WHERE secretary.del_flag = 0

  and secretary_id = #{secretaryId,jdbcType=BIGINT}


  and secretary.dept_id = #{deptId}

limit 1

mapper.update:
UPDATE tab_pb_dept_secretary
SET del_flag = #{delFlag},
    update_time = #{updateTime},
    update_userid = #{updateUserid},
    update_username = #{updateUsername}
WHERE
    user_id = #{userId}
    AND lead_team_id = #{leadTeamId}
    AND del_flag = '0'

mapper.select:
SELECT s.secretary_id, u.user_id, u.realname, u.id_card_no, u.ancestor_place, u.nation, u.avatar2, u.gender, u.born_place, u.birthday, u.join_time, u.work_date, u.health, u.phone, u.avatar, s.first_committees_date, s.new_position, s.appointment_time, s.old_position, s.serve_time, s.professional_titles, s.professional_specialty, s.full_time_schooling, s.education, s.college_major, s.college_major_two, m.rank, d.NAME AS deptName FROM tab_pb_dept_secretary s INNER JOIN tab_pb_lead_team t ON s.lead_team_id = t.lead_team_id INNER JOIN tab_pb_lead_team_member m ON m.lead_team_id = t.lead_team_id INNER JOIN sys_user u ON u.user_id = s.user_id and u.user_id = m.user_id INNER JOIN sys_dept d ON d.dept_id = t.org_id
s.del_flag = '0' AND m.del_flag = '0' AND u.ebl_flag = '1' AND m.del_flag='0' AND u.del_flag = '0'
AND u.realname like CONCAT('%',#{realname},'%')
AND u.id_card_no = #{idCardNo}
AND s.new_position = #{positiveName}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}
