mapper.select:
select '14' as attachment_type, l.live_id, l.region, l.plan, l.name, l.description, l.cover, l.res_address, l.start_time, l.end_time, l.address, l.teacher_id, l.type, l.del_flag, l.order_num, l.create_time, l.create_userid, l.create_username, l.update_time, l.update_userid, l.update_username, l.record_id,l.is_top, (SELECT COUNT(*) FROM tab_pb_edu_like as click WHERE click.like_type = 2 AND click.del_flag = 0 AND click.bus_id = l.live_id) AS sumClick, ( SELECT COUNT(*) FROM tab_pb_edu_msg_board as msg WHERE msg.msg_type = 2 AND msg.bus_id = l.live_id) AS sumMsg, (SELECT COUNT(*) FROM tab_pb_edu_subscribe as subs WHERE subs.like_type = 2 AND subs.del_flag = 0 AND subs.bus_id = l.live_id) AS sumSubscribe,
CASE
WHEN NOW() >= end_time THEN '59102' WHEN NOW() >= start_time AND NOW() < end_time THEN '59101' WHEN date_add(start_time,interval -30 minute) < now() AND now() < start_time THEN '59100' WHEN NOW() < date_add(start_time,interval -30 minute) THEN '59099' ELSE '0'
END as state,
(select `name` from tab_pb_edu_teachers as teacher where teacher.teacher_id = l.teacher_id) as teacherName from tab_pb_edu_live as l where 1=1
AND l.live_id = #{liveId}
AND l.name like concat(concat("%",#{name,jdbcType=VARCHAR}),"%")
AND l.plan = #{plan}
AND l.region = #{region}
AND l.teacher_id = #{teacherId}
AND l.del_flag = 0 GROUP BY l.live_id order by l.is_top desc ,l.start_time desc
