mapper.update:
UPDATE tab_pb_edu_study_center esc
set esc.del_flag = 1
where esc.study_id = #{studyId}

update tab_pb_edu_study_center esc
set esc.like_count = esc.like_count + 1
where esc.study_id = #{studyId}

update tab_pb_edu_study_center esc
set esc.watch_count = esc.watch_count + 1
where esc.study_id = #{studyId}
