mapper.select:
select course.* from tab_pb_edu_teachers_course course
where course.del_flag = 0

  and  course.teacher_id = #{teacherId}

mapper.insert:
insert into tab_pb_edu_teachers_course (id, teacher_id, title, 
  del_flag, order_num, create_time, 
  create_userid, create_username, update_time, 
  update_userid, update_username, outline
  )
values (#{id,jdbcType=BIGINT}, #{teacherId,jdbcType=BIGINT}, #{title,jdbcType=VARCHAR}, 
  #{delFlag,jdbcType=VARCHAR}, #{orderNum,jdbcType=BIGINT}, #{createTime,jdbcType=TIMESTAMP}, 
  #{createUserid,jdbcType=BIGINT}, #{createUsername,jdbcType=VARCHAR}, #{updateTime,jdbcType=TIMESTAMP}, 
  #{updateUserid,jdbcType=BIGINT}, #{updateUsername,jdbcType=VARCHAR}, #{outline,jdbcType=LONGVARCHAR}
  )
