mapper.select:
SELECT tea.* FROM tab_pb_edu_teachers tea
WHERE tea.del_flag = '0'
and teacher_id = #{teacherId,jdbcType=BIGINT}

SELECT tea.*,att.attachment_instance FROM tab_pb_edu_teachers tea
LEFT JOIN tab_pb_attachment att ON tea.teacher_id = att.host_id AND att.attachment_type = ${@com.yizheng.commons.util.AttachmentType@TEACHERS_MANAGER}
and att.del_flag = '0'
WHERE tea.del_flag = '0'

    and region = #{region}


    and teacher_type = #{teacherType}


    and name LIKE CONCAT('%',#{name},'%')
