mapper.select:
SELECT lead.*,d.name as deptName,u.gender,u.phone FROM `tab_pb_lead_team_member` as lead LEFT JOIN sys_dept as d on lead.org_id=d.dept_id and d.del_flag = 0 LEFT JOIN sys_user AS u on lead.user_id = u.user_id and u.del_flag = 0 WHERE ( lead.as_committee_member = 1 OR lead.as_committee_director = 1 ) AND lead.del_flag = 0
AND lead.person_name like concat(concat("%",#{personName,jdbcType=VARCHAR}),"%")
AND lead.positive_id = #{positiveId,jdbcType=BIGINT}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}

SELECT lt.*, a.attachment_instance as pictures, u.id_card_no as idCardNo, u.user_id, u.gender, u.phone, d.name as deptName FROM tab_pb_lead_team_member lt LEFT JOIN sys_user u ON lt.user_id = u.user_id LEFT JOIN tab_pb_lead_team t on lt.lead_team_id =t.lead_team_id left join tab_pb_attachment a on host_id =lt.member_id AND a.attachment_type ='38' AND a.del_flag ='0' LEFT JOIN sys_dept d on t.org_id=d.dept_id where lt.del_flag=0
AND lt.person_name like concat(concat("%",#{personName,jdbcType=VARCHAR}),"%")
AND u.id_card_no like concat(concat("%",#{idCardNo,jdbcType=VARCHAR}),"%")
AND lt.rank =#{rank}
and lt.positive_id =#{positiveId}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}
