mapper.select:
select r.user_id,u.realname,u.id_card_no,u.gender,u.nation,r.dept_id ,d.name as in_where_dept_name,TIMESTAMPDIFF(YEAR, u.join_org_time,r.reduce_time) AS age,r.update_time,r.reduce_time,r.out_type,r.quit_type from tab_pb_member_reduce_list r JOIN sys_user u on r.user_id = u.user_id JOIN sys_dept d on r.dept_id = d.dept_id where r.del_flag = '0'
AND (d.dept_id = #{deptId} OR d.parent_id = #{deptId})
AND find_in_set(#{deptId}, d.full_path)
AND d.dept_id = #{deptId}
AND r.dept_id = #{deptId}
AND u.realname like concat('%', #{realName}, '%')
AND r.out_type = #{outType}
AND r.quit_type = #{quitType}
ORDER BY r.update_time DESC

select r.user_id,TIMESTAMPDIFF(YEAR, r.reduce_time, r.update_time) AS age from tab_pb_member_reduce_list r JOIN sys_user u on r.user_id = u.user_id JOIN sys_dept d on r.dept_id = d.dept_id where r.del_flag = '1'
AND (d.dept_id = #{deptId} OR d.parent_id = #{deptId})
AND find_in_set(#{deptId}, d.full_path)
AND d.dept_id = #{deptId}
AND r.dept_id = #{deptId}
ORDER BY r.update_time DESC
