mapper.update:
UPDATE tab_pb_message_send SET
send_org_id = #{a.sendOrgId}, send_org_name = #{a.sendOrgName}, type = #{a.type}, title = #{a.title}, content = #{a.content}, send_time = #{a.sendTime}, del_flag = #{a.delFlag}
WHERE
send_id = #{a.sendId}
AND del_flag = '0'

UPDATE tab_pb_message_receive
  SET receive_user_id = #{receiveUserId},
  receive_user_name = #{receiveUserName}
  WHERE
      send_id = #{sendId}

update.foreach:
UPDATE tab_pb_message_receive
send_id = #{a.sendId}, receiver_id = #{a.receiverId}, receiver_name = #{a.receiverName}, receiver_type = #{a.receiverType}
WHERE send_id = #{a.sendId}

mapper.update:
UPDATE tab_pb_message_receive
SET receive_status = 1,
    receive_time = now()
WHERE
    send_id = #{sendId}
    AND receiver_id = #{receiverId}

UPDATE tab_pb_message_receive mr
INNER JOIN tab_pb_message_send ms ON mr.send_id = ms.send_id
SET mr.receive_status = 0
WHERE ms.send_id IN

    ${item}

  AND mr.receiver_id = #{receiverId}

mapper.select:
SELECT s.send_id AS sendId, s.sender_id AS senderId, s.sender_name AS senderName, s.type AS type, s.title AS title, s.content AS content, s.send_time AS sendTime, r.receive_status AS receiveStatus, r.receiver_type AS receiverType, r.receiver_id AS receiverId FROM tab_pb_message_send s LEFT JOIN tab_pb_message_receive r ON s.send_id = r.send_id LEFT JOIN sys_dept d ON d.dept_id = r.receiver_id WHERE s.del_flag = 0
AND date_format(s.send_time, '%Y-%m-%d') >= #{sendTimeStart}
AND date_format(s.send_time, '%Y-%m-%d') <= #{sendTimeEnd}
AND s.title LIKE CONCAT('%', #{title}, '%')
AND s.type = #{type}
AND r.receive_status = #{receiveStatus}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}
ORDER BY s.send_time DESC
