mapper.select:
SELECT mup.*, ifnull(t.video_num, 0) videoNum, ifnull(t.img_num, 0) imgNum, ifnull(t.doc_num, 0) docNum FROM tab_pb_msg_up_info mup LEFT JOIN sys_dept d on d.dept_id=mup.real_dept_id LEFT JOIN view_attachment_count t on mup.id = t.host_id and t.attachment_type = 40
mup.del_flag = 0
and mup.id=#{id}
and mup.up_time between #{upTime} and #{upTimeOver}
and mup.title_label=#{titleLabel}
and mup.recevie_dept_id=#{recevieDeptId}
and mup.up_dept_name=#{upDeptId}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}
order by mup.id desc

SELECT mup.id, mup.up_type, mup.up_dept_id, mup.up_dept_name, mup.up_user_id, mup.up_username, mup.recevie_dept_id, mup.recevie_dept_name, mup.recevie_user_id, mup.recevie_username, mup.title, mup.up_time, mup.title_label, mup.content, mup.audit_dept_id, mup.audit_user_id, mup.audit_time, mup.audit_comment, mup.audit_assess, mup.audit_dept_name, mup.audit_username, mup.real_dept_id, mup.real_dept_name, ifnull(t.video_num, 0) videoNum, ifnull(t.img_num, 0) imgNum, ifnull(t.doc_num, 0) docNum FROM tab_pb_msg_up_info mup LEFT JOIN sys_dept d on d.dept_id=mup.recevie_dept_id LEFT JOIN view_attachment_count t on mup.id = t.host_id and t.attachment_type = 40
mup.del_flag = 0
and mup.up_time between #{upTime} and #{upTimeOver}
and mup.title_label=#{titleLabel}
AND (d.dept_id = #{rangeDeptId} OR d.parent_id = #{rangeDeptId})
AND find_in_set(#{rangeDeptId}, d.full_path)
AND d.dept_id = #{rangeDeptId}
order by mup.id desc

