mapper.select:
SELECT A.*, (SELECT `name` FROM sys_dept WHERE dept_id = A.now_superior_id) AS now_superior_name, (SELECT `name` FROM sys_dept WHERE dept_id = A.old_superior_id) AS old_superior_name FROM tab_pb_orgnize_change A WHERE 1=1
and A.change_type = #{changeType}
AND A.dept_id = #{deptId}
ORDER BY A.update_time DESC LIMIT 1

select t.*, (select d.name from sys_dept d where d.dept_id = t.now_superior_id) as now_superior_name, (select d.org_short_name from sys_dept d where d.dept_id = t.dept_id) as short_name from tab_pb_orgnize_change t where del_flag='0'
and change_type in
#{item}
and dept_id = #{deptId}
order by change_date asc
