mapper.update:
update tab_pb_party_membership(identity_type, type, reason, create_time, create_userid, create_username)
set identity_type = #{identityType, jdbcType=BIGINT}, type = #{type, jdbcType=BIGINT}, reason = #{reason, jdbcType=VARCHAR}, create_time = #{createTime, jdbcType=TIMESTAMP}, create_userid = #{createUserid, jdbcType=BIGINT}, create_username = #{createUsername, jdbcType=VARCHAR}
where user_id = #{userId, jdbcType=BIGINT}

mapper.select:
select * from tab_pb_party_membership


        identity_type = #{identityType, jdbcType=BIGINT}


        and type = #{type, jdbcType=BIGINT}


ORDER BY create_time DESC
