mapper.select:
select
,d.label as positive_name_dict,d.sort from tab_pb_positives p left join sys_dict d on d.id = p.positive_name where p.del_flag = 0
and p.positive_type = #{positiveType}
and p.user_id = #{userId}

select

from tab_pb_positives p
where del_flag = 0

    and positive_type=#{positiveType}


    and user_id = #{userId}

select

,d.label as positive_name_dict
from tab_pb_positives p
left join sys_dict d on d.id = p.positive_name
where p.del_flag = 0

    and p.positive_name = #{positiveName}


    and p.user_id = #{userId}


    and p.positive_org_id = #{positiveOrgId}

mapper.update:
UPDATE tab_pb_positives
SET del_flag = 1,
update_time = #{updateTime}, update_userid = #{updateUserid}, updateUsername = #{updateUsername}
WHERE
    positive_org_id = #{positiveOrgId} and user_id = #{userId} and business_from = #{business} and business_id = #{businessId}
